package a;

public class B {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<10; i++) {
			if(i==4 | i==5) {
				System.out.println("*");
				//별표 하나 출력해주세요~
			}//i가 4거나 5일 때, 
			System.out.println(i);//숫자 출력
		//깃에 올리기. 'A' 숙제도.
	}//10번 동안 반복
	}

}
